mapper.select:
		
		select
		
		from cms_channel
		
                
                    site_id = #{siteId} and pid = 0
                
                
                    and pid = #{channelId}
                
                
                  and id not in
	                   
					    #{item}  
					 
			    
			     order by ${order}

SELECT DISTINCT t.id,t.pid,t.display_name,t.en_name,t.site_id,t.site_domain,t.template_id, t.is_html,t.seq, t.create_time, t.create_user,t.update_time,t.forbidden,t.page_size,url,channel_type FROM cms_channel t LEFT JOIN t_group_channel gc ON t.id = gc.CHANNEL_ID LEFT JOIN t_group_user gu ON gc.GROUP_ID = gu.GROUP_ID WHERE gu.USER_CODE=#{uid}
and t.pid=#{pid}
and site_id = #{model.siteId}
and (pid = #{model.id} or id = #{model.id})
and display_name like CONCAT(CONCAT('%', #{model.displayName}),'%')
and forbidden = #{model.forbidden}
order by t.seq

SELECT DISTINCT t.id, t.pid, t.display_name,t.site_id,t.channel_type FROM cms_channel t LEFT JOIN t_group_channel gc ON t.id = gc.CHANNEL_ID LEFT JOIN t_group_user gu ON gc.GROUP_ID = gu.GROUP_ID WHERE 1=1
and gu.USER_CODE=#{uid}
and forbidden = #{forbidden}
and site_id = #{model.siteId}
and channel_type = #{model.channelType}
and pid = #{model.pid}
and display_name like CONCAT(CONCAT('%', #{model.displayName}),'%')
order by t.seq


