mapper.select:
        
        select
        
        from cms_content
        where channel_id in
			  
			  #{item}  
			  
			 
			 	AND is_html = #{isHtml}
			 
			 
			 	order by ${order}

        select
        
        ,
        
        from cms_content
        
        	channel_id in
			  
			  #{item}  
			 
            
                
                    and site_domain = #{model.siteDomain}
                
                
                    and title like CONCAT(CONCAT('%', #{model.title}),'%')
                
                
                	and status = #{model.status}
                
                
                	and is_html = #{model.isHtml}
                
            
        
        
        	order by ${order}

        select
        
        ,
        
        from cms_content
        
        	channel_id  = #{channelId}
        	
		        and status in
		        
					#{item}
				
      		
        
        
        	order by ${order}
        
        limit #{pageStart},#{pageSize}

select
,
from cms_content where id=#{contentId} and site_domain = #{domain} and channel_en_name = #{enName}

	SELECT GROUP_CONCAT(content_id)
    FROM cms_content_attachment 
    where attachment_id = #{id}
