mapper.select:
SELECT s.id siteId, s.url AS url, s.display_name AS title, Count(vr.id) AS count FROM cms_site AS s LEFT JOIN cms_channel AS c ON s.id = c.site_id LEFT JOIN cms_content_visit_record AS vr ON c.id = vr.channel_id WHERE vr.content_id is NULL
and vr.visit_time > #{date}
and s.id=#{siteId} GROUP BY s.id ORDER BY ${sort}

SELECT chan.id channelId, chan.url AS url, chan.display_name AS title,IFNULL(v.count,0) count FROM cms_channel chan LEFT JOIN (SELECT c.id channelId, count(vr.id) AS count FROM cms_channel c
LEFT JOIN cms_site AS s ON s.id = c.site_id
LEFT JOIN cms_content_visit_record vr ON c.id = vr.channel_id WHERE vr.content_id is NULL AND vr.channel_id is NOT NULL
and vr.visit_time > #{date}
AND s.id = #{siteId}
AND c.id = #{channelId}
GROUP BY vr.channel_id ) v ON chan.id=v.channelId
ORDER BY ${sort}

SELECT ch.display_name AS title,DATE_FORMAT( c.publish_date, "%Y-%m-%d" ) AS date, COUNT(c.id) as count FROM cms_content c LEFT JOIN cms_channel ch ON c.channel_id = ch.id WHERE c.publish_date IS NOT NULL AND c.publish_date > #{vo.date} AND ch.id IN
#{channelId}
GROUP BY ch.id,DATE_FORMAT( c.publish_date, "%Y-%m-%d" ) ORDER BY date ASC

SELECT ch.id AS channelId,ch.display_name AS title, COUNT(c.id) as count FROM cms_content c LEFT JOIN cms_channel ch ON c.channel_id = ch.id WHERE c.publish_date IS NOT NULL AND c.publish_date > #{date} AND c.is_html=1
AND ch.site_id = #{siteId}
GROUP BY ch.id ORDER BY count DESC

SELECT `USER`.`NAME` name,`USER`.`CODE` code,COUNT(v.id) as count FROM t_user_account USER LEFT JOIN cms_content_visit_record v ON `USER`.`CODE` = v.user_id
user.code = #{code}
GROUP BY `USER`.`CODE`
ORDER BY ${order}
