mapper.select:
select t0.id,t0.template_name,t0.template_path,t0.template_blob_id,t0.create_time, t0.create_user,t0.update_time,t0.template_type,t0.site_id,t.display_name as site_name from cms_template t0 left join cms_site t on site_id=t.id
1=1
and t0.template_name like CONCAT(CONCAT('%', #{model.displayName}),'%')
and domain like CONCAT(CONCAT('%',#{model.dmoain}),'%')
and t0.site_id=#{siteId}
and t0.template_type=#{templateType}
ORDER BY ${sort}
