mapper.insert:
insert into cms_site (id, display_name, domain, 
  template_id, is_html, create_time, 
  create_user, update_time, url
  )
values (#{id,jdbcType=INTEGER}, #{displayName,jdbcType=VARCHAR}, #{domain,jdbcType=VARCHAR}, 
  #{templateId,jdbcType=INTEGER}, #{isHtml,jdbcType=BIT}, #{createTime,jdbcType=TIMESTAMP}, 
  #{createUser,jdbcType=INTEGER}, #{updateTime,jdbcType=TIMESTAMP}, #{url,jdbcType=VARCHAR}
  )

mapper.select:
select 

from cms_site
where create_time < #{createTime,jdbcType=TIMESTAMP} and update_time >= #{updateTime,jdbcType=TIMESTAMP}
